import React, { Component } from "react";
import logo from "../untied_logo.png";
import {
  taxYear_2019to2020 as _19to20,
  taxYear_2020to2021 as _20to21,
} from "../reusableCalculator";

class TaxCalculator extends Component {
  state = { calculate: "takeHome", taxYear: _19to20 };

  taxYearSelect = (taxYear) => {
    this.setState({ taxYear });
  };

  renderTable = () => {
    const { calculate, income } = this.state;
    const salary_19to20 =
      calculate === "takeHome" ? income : _19to20.salaryFromTakeHome(income);
    const nI_19to20 = _19to20.nationalInsurance(salary_19to20);
    const incomeTax_19to20 = _19to20.incomeTax(salary_19to20);
    const takeHome_19to20 =
      calculate === "takeHome" ? _19to20.takeHomePay(income) : income;

    const salary_20to21 =
      calculate === "takeHome" ? income : _20to21.salaryFromTakeHome(income);
    const nI_20to21 = _20to21.nationalInsurance(salary_20to21);
    const incomeTax_20to21 = _20to21.incomeTax(salary_20to21);
    const takeHome_20to21 =
      calculate === "takeHome" ? _20to21.takeHomePay(income) : income;
  };

  render() {
    console.log(this.state);
    const { taxYear, income } = this.state;
    return (
      <React.Fragment>
        <div className="logoDiv">
          <img src={logo} className="logo" />
        </div>
        <div className="mainDiv">
          <h1>Welcome to the Untied tax calculator</h1>

          <h3>First, select which tax year you want</h3>
          <div
            style={{
              width: "75%",
              margin: "auto",
              //   display: "flex",
              //   justifyContent: "space-around",
            }}
          >
            <span
              style={{ marginRight: "5%" }}
              onChange={() => this.taxYearSelect(_19to20)}
            >
              <input type="radio" id="19/20" name="taxYear" value="19/20" />
              <label for="19/20">2019/2020</label>
            </span>

            <span
              style={{ marginLeft: "5%" }}
              onChange={() => this.taxYearSelect(_20to21)}
            >
              <input type="radio" id="20/21" name="taxYear" value="20/21" />
              <label for="20/21">2020/2021</label>
            </span>
          </div>
          <h3>Now select what you would like to calculate</h3>
          <div
            style={{
              width: "75%",
              margin: "auto",
              //   display: "flex",
              //   justifyContent: "space-around",
            }}
          >
            <span
              style={{ marginRight: "5%" }}
              onChange={() => this.setState({ calculate: "takeHome" })}
            >
              <input
                type="radio"
                id="takeHome"
                name="calculate"
                value="takeHome"
                checked="checked"
              />
              <label for="takeHome">Take-home pay from salary</label>
            </span>

            <span
              style={{ marginLeft: "5%" }}
              onChange={() => this.setState({ calculate: "salary" })}
            >
              <input type="radio" id="salary" name="calculate" value="salary" />
              <label for="salary">Salary from take-home pay</label>
            </span>
          </div>
          <br />
          <label for="income" style={{ marginRight: "1%" }}>
            {(this.state.calculate === "salary" &&
              "What is your desired take-home pay?") ||
              "What is your current salary?"}
            {" (GBP)"}
          </label>
          <input
            id="income"
            type="number"
            min={0}
            step="1000"
            style={{ marginLeft: "1%" }}
            onChange={(e) =>
              this.setState({ income: parseInt(e.currentTarget.value) })
            }
          ></input>
          <button>Calculate</button>
          <h3>{taxYear.takeHomePay(income)}</h3>
        </div>
      </React.Fragment>
    );
  }
}

export default TaxCalculator;
