import React, { Component } from "react";
import logo from "../untied_logo.png";
import { taxYear_2019to2020, taxYear_2020to2021 } from "../reusableCalculator";

class TaxCalculator extends Component {
  state = { calculate: "takeHome", taxYear: taxYear_2019to2020 };

  taxYearSelect = (taxYear) => {
    this.setState({ taxYear });
  };

  render() {
    console.log(this.state);
    return (
      <React.Fragment>
        <div className="logoDiv">
          <img src={logo} className="logo" />
        </div>
        <div className="mainDiv">
          <h1>Welcome to the Untied tax calculator</h1>

          <h3>First, select which tax year you want</h3>
          <div
            style={{
              width: "75%",
              margin: "auto",
              //   display: "flex",
              //   justifyContent: "space-around",
            }}
          >
            <span
              style={{ marginRight: "5%" }}
              onChange={() => this.taxYearSelect(taxYear_2019to2020)}
            >
              <input type="radio" id="19/20" name="taxYear" value="19/20" />
              <label for="19/20">2019/2020</label>
            </span>

            <span
              style={{ marginLeft: "5%" }}
              onChange={() => this.taxYearSelect(taxYear_2020to2021)}
            >
              <input type="radio" id="20/21" name="taxYear" value="20/21" />
              <label for="20/21">2020/2021</label>
            </span>
          </div>
          <h3>Now select what you would like to calculate</h3>
          <div
            style={{
              width: "75%",
              margin: "auto",
              //   display: "flex",
              //   justifyContent: "space-around",
            }}
          >
            <span
              style={{ marginRight: "5%" }}
              onChange={() => this.setState({ calculate: "takeHome" })}
            >
              <input
                type="radio"
                id="takeHome"
                name="calculate"
                value="takeHome"
                checked="checked"
              />
              <label for="takeHome">Take-home pay from salary</label>
            </span>

            <span
              style={{ marginLeft: "5%" }}
              onChange={() => this.setState({ calculate: "salary" })}
            >
              <input type="radio" id="salary" name="calculate" value="salary" />
              <label for="salary">Salary from take-home pay</label>
            </span>
          </div>
          <br />
          <label for="income" style={{ marginRight: "1%" }}>
            {(this.state.calculate === "salary" &&
              "What is your desired take-home pay?") ||
              "What is your current salary?"}
            {" (GBP)"}
          </label>
          <input
            id="income"
            type="number"
            min={0}
            step="1000"
            style={{ marginLeft: "1%" }}
            onChange={(e) =>
              this.setState({ income: parseInt(e.currentTarget.value) })
            }
          ></input>
          <button>Calculate</button>
          <h3>{this.state.taxYear.takeHomePay(parseInt(this.state.income))}</h3>
        </div>
      </React.Fragment>
    );
  }
}

export default TaxCalculator;
