import React, { Component } from "react";
import logo from "../untied_logo.png";

import TaxTable from "./taxTable";

class TaxCalculator extends Component {
  state = { calculation: "takeHome", query: 0 };

  renderInputLabel() {
    if (calculation === "salary") return "What is your desired take-home pay?";
    return "What is your current salary?";
  }

  render() {
    const { query, calculation } = this.state;
    return (
      <React.Fragment>
        <div className="logoDiv">
          <img src={logo} className="logo" />
        </div>
        <div className="mainDiv">
          <h1>Welcome to the Untied tax calculator</h1>
          <hr />
          <h2>With this calculator you can either:</h2>
          <ul>
            <li>Calculate your take-home pay from a salary</li>
            <li>
              See what salary you should demand in order to acheive a certain
              take-home pay
            </li>
          </ul>
          <hr />
          <h3>Select what you would like to calculate:</h3>
          <div className="calculationSelect">
            <span
              className="leftCalculationButton"
              onChange={() => this.setState({ calculation: "takeHome" })}
            >
              <input
                type="radio"
                id="takeHome"
                name="calculation"
                value="takeHome"
                defaultChecked
              />
              <label for="takeHome">Take-home pay from salary</label>
            </span>

            <span
              className="rightCalculationButton"
              onChange={() => this.setState({ calculation: "salary" })}
            >
              <input
                type="radio"
                id="salary"
                name="calculation"
                value="salary"
              />
              <label for="salary">Salary from take-home pay</label>
            </span>
          </div>
          <br />
          <label for="income" style={{ marginRight: "1%" }}>
            {this.renderInputLabel()}
            {" (GBP)"}
          </label>
          <input
            id="income"
            type="number"
            min={0}
            step="1000"
            style={{ marginLeft: "1%" }}
            onChange={(e) =>
              this.setState({
                query: Math.abs(parseInt(e.currentTarget.value)),
              })
            }
          />
          <TaxTable query={query} calculation={calculation} />
        </div>
      </React.Fragment>
    );
  }
}

export default TaxCalculator;
