import React, { Component } from "react";
import logo from "../untied_logo.png";
import {
  taxYear_19to20 as _19to20,
  taxYear_20to21 as _20to21,
} from "../reusableCalculator";

class TaxCalculator extends Component {
  state = { calculate: "takeHome", query: 0 };

  render() {
    return (
      <React.Fragment>
        <div className="logoDiv">
          <img src={logo} className="logo" />
        </div>
        <div className="mainDiv">
          <h1>Welcome to the Untied tax calculator</h1>
          <hr />
          <h2>With this calculator you can either:</h2>
          <ul style={{ listStyleType: "none" }}>
            <li>Calculate your take-home pay from a salary</li>
            <li>
              See what salary you should demand in order to acheive a certain
              take-home pay
            </li>
          </ul>
          <hr />
          <h3>Select what you would like to calculate:</h3>
          <div
            style={{
              width: "75%",
              margin: "auto",
              //   display: "flex",
              //   justifyContent: "space-around",
            }}
          >
            <span
              style={{ marginRight: "5%" }}
              onChange={() => this.setState({ calculate: "takeHome" })}
            >
              <input
                type="radio"
                id="takeHome"
                name="calculate"
                value="takeHome"
                defaultChecked
              />
              <label for="takeHome">Take-home pay from salary</label>
            </span>

            <span
              style={{ marginLeft: "5%" }}
              onChange={() => this.setState({ calculate: "salary" })}
            >
              <input type="radio" id="salary" name="calculate" value="salary" />
              <label for="salary">Salary from take-home pay</label>
            </span>
          </div>
          <br />
          <label for="income" style={{ marginRight: "1%" }}>
            {(this.state.calculate === "salary" &&
              "What is your desired take-home pay?") ||
              "What is your current salary?"}
            {" (GBP)"}
          </label>
          <input
            id="income"
            type="number"
            min={0}
            step="1000"
            style={{ marginLeft: "1%" }}
            onChange={(e) =>
              this.setState({
                query: Math.abs(parseInt(e.currentTarget.value)),
              })
            }
          ></input>
          {this.renderTable()}
        </div>
      </React.Fragment>
    );
  }
}

export default TaxCalculator;

